// ========= Helpers =========
const r2 = (n) => Math.round(n * 100) / 100;
const asObj = (x) => { try { return typeof x === 'string' ? JSON.parse(x) : x; } catch { return null; } };
const normKey = (k) => String(k||'').toLowerCase().trim().normalize('NFD').replace(/[\u0300-\u036f]/g,'');
const isNum = (x) => typeof x === 'number' && Number.isFinite(x);
const isPlainObj = (x) => x && typeof x === 'object' && !Array.isArray(x);
// A veces n8n trae los JSON envueltos en array (Always Output Array)
const unwrapOne = (v) => (Array.isArray(v) && v.length === 1 ? v[0] : v);

// ========= Entradas y estados =========
const inputs = $input.all();

let cryptoData = null;          // CoinGecko (opcional)
let exchangeData = null;        // FX (varios formatos)
let sentimentPoints = null;     // Alternative.me (data[])
let fgFoundPath = null;         // debug opcional
const sheetRows = [];           // Filas normalizadas del Sheet
globalThis._fgSingles = [];     // buffer para puntos F&G sueltos

// ---------- Normalizadores para el Sheet ----------
const mapRow = (r) => {
  // normaliza claves a minÃºscula sin acentos
  const o = {};
  for (const [k, v] of Object.entries(r)) o[normKey(k)] = v;

  // mapea sinÃ³nimos
  const name = (o.portafolio ?? o.portfolio ?? o.asset ?? o.coin ?? '').toString().trim();
  const qty  = o.quantity ?? o.qty ?? o.amount ?? o.cantidad;
  const buy  = o.buy_price ?? o.buyprice ?? o.cost ?? o.price;
  const cur  = (o.currency ?? o.divisa ?? 'USD').toString().trim();
  const st   = (o.state ?? o.status ?? '').toString().trim();

  return { Portafolio: name, quantity: qty, buy_price: buy, currency: cur, state: st };
};

// Convierte CSV plano a objetos normalizados
const parseCSV = (text) => {
  const lines = text.trim().split(/\r?\n/);
  const headers = lines[0].split(',').map(h => normKey(h));
  const out = [];
  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;
    const vals = line.split(',').map(x => x.trim());
    const row = {};
    headers.forEach((h, idx) => row[h] = vals[idx]);
    out.push(mapRow(row));
  }
  return out;
};

// ---------- DetecciÃ³n hiper-robusta de Fear & Greed ----------
const looksLikeFGPoint = (x) =>
  x && typeof x === 'object' &&
  ('value' in x) && ('value_classification' in x) && (x.timestamp !== undefined);

const findFGPointsDeep = (root, maxDepth = 8) => {
  const seen = new Set();
  const stack = [{ node: root, path: '$', depth: 0 }];
  while (stack.length) {
    const { node, path, depth } = stack.pop();
    if (node == null) continue;

    // Si es string, intenta parsear JSON y seguir
    if (typeof node === 'string') {
      const parsed = asObj(node);
      if (parsed && parsed !== node) {
        stack.push({ node: parsed, path: path + '(parsed)', depth });
      }
      continue;
    }

    if (seen.has(node)) continue;
    if (typeof node !== 'object') continue;
    seen.add(node);

    // Caso directo: array de puntos
    if (Array.isArray(node) && node.length && looksLikeFGPoint(node[0])) {
      return { points: node, foundAt: path + '[*]' };
    }

    // Caso clÃ¡sico: { name:'Fear and Greed Index', data:[...] }
    if (node.name === 'Fear and Greed Index' && Array.isArray(node.data) && node.data.length && looksLikeFGPoint(node.data[0])) {
      return { points: node.data, foundAt: path + '.data' };
    }

    // Variante: { data:{ name:'...', data:[...] } }
    if (isPlainObj(node.data) && node.data.name === 'Fear and Greed Index' &&
        Array.isArray(node.data.data) && node.data.data.length && looksLikeFGPoint(node.data.data[0])) {
      return { points: node.data.data, foundAt: path + '.data.data' };
    }

    // Wrapper comÃºn: { data:[...] } sin 'name'
    if (Array.isArray(node.data) && node.data.length && looksLikeFGPoint(node.data[0])) {
      return { points: node.data, foundAt: path + '.data' };
    }

    if (depth >= maxDepth) continue;

    // Seguir recorriendo (en arrays, recorremos elementos; en objetos, sus valores)
    if (Array.isArray(node)) {
      for (let i = 0; i < node.length; i++) {
        stack.push({ node: node[i], path: `${path}[${i}]`, depth: depth + 1 });
      }
    } else {
      for (const [k, v] of Object.entries(node)) {
        stack.push({ node: v, path: `${path}.${k}`, depth: depth + 1 });
      }
    }
  }
  return null;
};

// ========= RecolecciÃ³n de entradas (CoinGecko / FX / Fear & Greed / Sheet) =========
for (const input of inputs) {
  const dataRaw = input.json ?? {};
  const data = unwrapOne(dataRaw);

  // CoinGecko (simple/price â€” opcional)
  if (
    data?.bitcoin?.usd !== undefined ||
    data?.ethereum?.usd !== undefined ||
    (isPlainObj(data) && Object.values(data).some(v => v && typeof v.usd === 'number'))
  ) cryptoData = data;

  // Tipos de cambio (varios proveedores)
  if (data?.rates || data?.result || data?.conversion_rates) exchangeData = data;

  // Fear & Greed: bÃºsqueda profunda en el item y en body
  if (!sentimentPoints) {
    const hit =
      findFGPointsDeep(data) ||
      findFGPointsDeep(data?.body) ||
      findFGPointsDeep(dataRaw) ||
      findFGPointsDeep(dataRaw?.body);
    if (hit && hit.points?.length) {
      sentimentPoints = hit.points;
      fgFoundPath = hit.foundAt;
    }
  }

  // ***NUEVO***: acumular puntos sueltos si cada item es un punto F&G
  if (looksLikeFGPoint(data)) {
    globalThis._fgSingles.push(data);
  }

  // ---------- Captura flexible de filas del Sheet ----------
  // 1) items planos ya correctos
  if ((data?.Portafolio !== undefined || data?.portfolio !== undefined || data?.Portfolio !== undefined) &&
      (data?.quantity !== undefined || data?.qty !== undefined || data?.cantidad !== undefined)) {
    sheetRows.push(mapRow(data));
  }

  // 2) arrays de objetos: rows / records / items / data
  for (const key of ['rows','records','items','data']) {
    if (Array.isArray(data?.[key]) && data[key].length && typeof data[key][0] === 'object' && !Array.isArray(data[key][0])) {
      for (const r of data[key]) sheetRows.push(mapRow(r));
    }
  }

  // 2.5) ***NUEVO***: si el item ES un array en la raÃ­z con objetos tipo fila
  if (Array.isArray(data) && data.length && typeof data[0] === 'object' && !Array.isArray(data[0])) {
    for (const r of data) sheetRows.push(mapRow(r));
  }

  // 3) array de arrays con 'values' (primera fila = headers)
  if (Array.isArray(data?.values) && Array.isArray(data.values[0])) {
    const headers = data.values[0].map(h => normKey(h));
    const idx = (name) => headers.indexOf(name);
    const idxP = idx('portafolio') !== -1 ? idx('portafolio') : (idx('portfolio') !== -1 ? idx('portfolio') : -1);
    const idxQ = idx('quantity') !== -1 ? idx('quantity') : (idx('qty') !== -1 ? idx('qty') : -1);
    const idxB = idx('buy_price') !== -1 ? idx('buy_price') : (idx('buyprice') !== -1 ? idx('buyprice') : -1);
    const idxC = idx('currency');
    const idxS = idx('state');
    if (idxP !== -1 && idxQ !== -1) {
      for (let i = 1; i < data.values.length; i++) {
        const r = data.values[i];
        sheetRows.push(mapRow({
          portafolio: r[idxP],
          quantity  : r[idxQ],
          buy_price : idxB !== -1 ? r[idxB] : undefined,
          currency  : idxC !== -1 ? r[idxC] : 'USD',
          state     : idxS !== -1 ? r[idxS] : undefined,
        }));
      }
    }
  }

  // 4) CSV crudo en texto
  if (typeof data === 'string' && /portafolio\s*,\s*quantity/i.test(data)) {
    sheetRows.push(...parseCSV(data));
  }
}

// Si no encontramos un array y sÃ­ hay puntos sueltos acumulados, Ãºsalos (ordenados por timestamp asc)
if (!sentimentPoints && Array.isArray(globalThis._fgSingles) && globalThis._fgSingles.length) {
  sentimentPoints = globalThis._fgSingles
    .slice()
    .sort((a,b) => Number(a.timestamp) - Number(b.timestamp));
}

// ========= Tipos de cambio (con fallback) =========
// Valor por defecto si no llega la API: 1 USD = 0.92 EUR
let usdToEur = 0.92;
if (exchangeData) {
  const rates = exchangeData.rates || exchangeData.result || exchangeData.conversion_rates || {};
  if (isNum(rates.EUR)) usdToEur = rates.EUR;
  // Si la base es EUR, y te dan USD, invierte
  if (exchangeData.base === 'EUR' && isNum(rates.USD)) usdToEur = 1 / rates.USD;
}
// sanidad
if (!isNum(usdToEur) || usdToEur <= 0) usdToEur = 0.92;
const eurToUsd = 1 / usdToEur;

// ========= ConstrucciÃ³n del portafolio =========
// Agrupa filas por moneda y guarda filas originales (para convertir buy_price segÃºn currency)
const portfolio = {};
for (const row of sheetRows) {
  const state = String(row.state ?? '').trim().toLowerCase();
  if (state && state !== 'registered') continue;

  let rawName = (row.Portafolio ?? '').toString().trim().toLowerCase();
  // si alguien puso "cardano 2", quÃ©date con el token alfabÃ©tico
  rawName = rawName.split(/[^\p{L}\-]+/u)[0];
  if (!rawName) continue;

  const qty = parseFloat(String(row.quantity).replace(',', '.'));
  if (!isFinite(qty) || qty <= 0) continue;

  const buyPriceRaw = parseFloat(String(row.buy_price ?? '').replace(',', '.'));
  const currency = String(row.currency ?? 'USD').trim().toUpperCase();
  const buyPrice = isFinite(buyPriceRaw) ? buyPriceRaw : 0;

  if (!portfolio[rawName]) portfolio[rawName] = { amount: 0, rows: [] };
  portfolio[rawName].amount += qty;
  portfolio[rawName].rows.push({ qty, buyPrice, currency });
}

const entries = Object.entries(portfolio);
if (entries.length === 0) {
  throw new Error("Portafolio vacÃ­o tras normalizaciÃ³n (revisa Portafolio/quantity en los items de entrada).");
}

// Convierte filas por moneda a costBasisUSD agregado (ahora que ya tenemos usdToEur/eurToUsd)
for (const [coin, agg] of Object.entries(portfolio)) {
  let costUSD = 0;
  for (const r of agg.rows) {
    let pUSD = r.buyPrice;
    if (r.currency === 'EUR') pUSD = r.buyPrice * eurToUsd;
    // (si usas otras divisas, conviÃ©rtelas aquÃ­)
    costUSD += pUSD * r.qty;
  }
  agg.costBasisUSD = costUSD;
}

// ========= CÃ¡lculos por moneda =========
let cryptoValueUSD = 0;
let totalCostBasisUSD = 0;
const cryptoDetails = [];

for (const [coin, agg] of entries) {
  const amount = agg.amount;

  const priceUSD = cryptoData?.[coin]?.usd ?? 0;
  const change24hCoin =
    (cryptoData?.[coin]?.usd_24h_change ??
     cryptoData?.[coin]?.price_change_percentage_24h ??
     0); // %

  const valueUSD = priceUSD * amount;

  cryptoValueUSD += valueUSD;
  totalCostBasisUSD += agg.costBasisUSD;

  const avgBuyPriceUSD = (agg.costBasisUSD > 0 && amount > 0) ? (agg.costBasisUSD / amount) : 0;
  const profitUSD = valueUSD - agg.costBasisUSD;
  const profitPercent = agg.costBasisUSD > 0 ? (profitUSD / agg.costBasisUSD) * 100 : null;

  const priceEUR = priceUSD * usdToEur;
  const valueEUR = valueUSD * usdToEur;
  const costBasisEUR = agg.costBasisUSD * usdToEur;
  const profitEUR = profitUSD * usdToEur;

  cryptoDetails.push({
    coin: coin.toUpperCase(),
    amount,
    priceUSD: r2(priceUSD),
    valueUSD: r2(valueUSD),
    priceEUR: r2(priceEUR),
    valueEUR: r2(valueEUR),
    change24h: r2(change24hCoin),   // %
    avgBuyPriceUSD: r2(avgBuyPriceUSD),
    costBasisUSD: r2(agg.costBasisUSD),
    profitUSD: r2(profitUSD),
    profitPercent: profitPercent !== null ? r2(profitPercent) : null,
    costBasisEUR: r2(costBasisEUR),
    profitEUR: r2(profitEUR),
  });
}

// ========= 24h REAL del portafolio (ponderado) =========
// Para cada cripto: prev = current / (1 + pct)
let totalCurrentEUR = 0;
let totalYesterdayEUR = 0;

for (const c of cryptoDetails) {
  const currentEUR = c.valueUSD * usdToEur;
  const pct = (c.change24h ?? 0) / 100;
  const prevEUR = (pct > -1) ? (currentEUR / (1 + pct)) : 0;
  totalCurrentEUR += currentEUR;
  totalYesterdayEUR += prevEUR;
}

// (Si agregas acciones, sÃºmalas aquÃ­ con su propio % 24h)
const change24hEUR = totalCurrentEUR - totalYesterdayEUR;
const changePercent = totalYesterdayEUR > 0 ? (change24hEUR / totalYesterdayEUR) * 100 : 0;

const totalUSD = cryptoValueUSD;
const totalEUR = totalCurrentEUR;

// ========= Sentiment 7D (Alternative.me) =========
let sentiment = null;
if (Array.isArray(sentimentPoints) && sentimentPoints.length) {
  const ptsNewestFirst = sentimentPoints.map(d => ({
    idx: parseInt(d.value, 10),
    label: String(d.value_classification),
    ts: new Date(parseInt(d.timestamp, 10) * 1000).toISOString(),
  }));
  const pts = [...ptsNewestFirst].reverse();     // viejo -> nuevo
  const values = pts.map(p => p.idx);

  const avg = values.reduce((a,b)=>a+b,0) / values.length;
  const min = Math.min(...values);
  const max = Math.max(...values);
  const start = values[0];                       // ~hace 7 dÃ­as
  const end   = values[values.length - 1];       // hoy
  const delta = end - start;
  const trendPct = start > 0 ? (delta / start) * 100 : 0;

  // Racha desde hoy hacia atrÃ¡s (1 sube, -1 baja)
  let streakDir = 0, streakLen = 1;
  for (let i = values.length - 1; i > 0; i--) {
    const diff = values[i] - values[i - 1];
    const dir = diff > 0 ? 1 : diff < 0 ? -1 : 0;
    if (streakDir === 0) streakDir = dir || streakDir;
    if (dir === streakDir && dir !== 0) streakLen++;
    else break;
  }

  sentiment = {
    today: { index: end, label: pts[pts.length - 1].label, timestamp: pts[pts.length - 1].ts },
    week:  {
      avg: r2(avg), min, max, delta, trendPct: r2(trendPct),
      fearDays: pts.filter(p => /fear/i.test(p.label)).length,
      greedDays: pts.filter(p => /greed/i.test(p.label)).length,
      streak: { direction: streakDir, length: streakLen },
      sparkline: values
    }
  };
}

// ========= Totales / PnL =========
const totalCostBasisEUR = (totalCostBasisUSD ?? 0) * usdToEur;
const totalProfitUSD = totalUSD - (totalCostBasisUSD ?? 0);
const totalProfitEUR = totalProfitUSD * usdToEur;
const totalProfitPercent = (totalCostBasisUSD ?? 0) > 0 ? (totalProfitUSD / totalCostBasisUSD) * 100 : null;

// ========= Salida =========
const out = {
  portfolio: {
    totalEUR: r2(totalEUR),
    totalCostBasisEUR: r2(totalCostBasisEUR),
    totalProfitEUR: r2(totalProfitEUR),
    totalProfitPercent: totalProfitPercent !== null ? r2(totalProfitPercent) : null,
    change24hEUR: r2(change24hEUR),
    changePercent: r2(changePercent),
    cryptoValueEUR: r2(totalCurrentEUR),
    stockValueEUR: 0,
  },
  crypto: cryptoDetails,
  exchangeRate: { usdToEur },
  sentiment,
  timestamp: new Date().toISOString(),
};

// Resumen corto (seguro aunque falte CoinGecko o FX)
out.summary = `Portfolio: â‚¬${Math.round(out.portfolio.totalEUR)} | 24h: ${out.portfolio.changePercent.toFixed(2)}% (${out.portfolio.change24hEUR >= 0 ? '+' : ''}â‚¬${Math.round(out.portfolio.change24hEUR)}) | PnL: â‚¬${Math.round(out.portfolio.totalProfitEUR)}${sentiment ? ` | F&G hoy: ${sentiment.today.index} (${sentiment.today.label})` : ''}`;

// (Opcional) Debug mÃ­nimo: descomenta para ver por dÃ³nde encontrÃ³ F&G y cuÃ¡ntas filas de sheet detectÃ³
 out._debug = { gotFG: !!sentimentPoints, fgFoundPath, sheetRows: sheetRows.length };

return [{ json: out, pairedItem: { item: 0 } }];

